using ( FileStream pdf = new FileStream( 
   "report1265.pdf", 
   FileMode.Open, 
   FileAccess.Read ) )
{
   Document document = new Document( pdf );
   using ( FileStream tiff = new FileStream( 
      "report1265.tif", 
      FileMode.Create, 
      FileAccess.Write ) )
   {       
      document.ConvertToTiff( tiff, new ConvertToTiffOptions( 300, TiffCompression.CcittG4 ) );
   }
}

